$(document).ready(function() {


    var height = $(window).height();
    var width = $(window).width();
    var st = $(window).scrollTop();

    $('#w').text(width);
    $('#h').text(height);
    $('#st').text(st);


    $(window).scroll(function() {

        var height = $(window).height();
        var width = $(window).width();
        var st = $(window).scrollTop();
        $('#w').text(width);
        $('#h').text(height);
        $('#st').text(st);
    });
});

